// Move file to correct location
export interface MediaFile {
  id: string;
  type: 'video' | 'audio' | 'image';
  name: string;
  src: string;
  proxy?: string;
  duration?: number;
  width?: number;
  height?: number;
  fps?: number;
  thumbnails?: string[];
  waveform?: number[];
  size: number;
  createdAt: Date;
}

export interface Clip {
  id: string;
  mediaId: string;
  trackId: string;
  startTime: number;
  endTime: number;
  sourceStartTime: number;
  sourceEndTime: number;
  speed: number;
  opacity: number;
  volume: number;
  muted: boolean;
  locked: boolean;
  transform: {
    x: number;
    y: number;
    scaleX: number;
    scaleY: number;
    rotation: number;
    anchorX: number;
    anchorY: number;
  };
  effects: Effect[];
  keyframes: Keyframe[];
}

export interface Track {
  id: string;
  type: 'video' | 'audio';
  name: string;
  locked: boolean;
  muted: boolean;
  solo: boolean;
  height: number;
  clips: Clip[];
}

export interface Effect {
  id: string;
  type: string;
  name: string;
  enabled: boolean;
  parameters: Record<string, any>;
}

export interface Keyframe {
  id: string;
  property: string;
  time: number;
  value: any;
  easing: 'linear' | 'ease-in' | 'ease-out' | 'ease-in-out';
}

export interface Transition {
  id: string;
  type: 'crossfade' | 'dip-to-black' | 'slide' | 'wipe';
  duration: number;
  startClipId: string;
  endClipId: string;
  parameters: Record<string, any>;
}

export interface Marker {
  id: string;
  time: number;
  name: string;
  color: string;
}

export interface Project {
  id: string;
  name: string;
  version: number;
  fps: number;
  width: number;
  height: number;
  duration: number;
  tracks: Track[];
  media: MediaFile[];
  effects: Effect[];
  transitions: Transition[];
  markers: Marker[];
  settings: {
    previewQuality: 'full' | 'half' | 'quarter';
    snapEnabled: boolean;
    rippleDelete: boolean;
    magneticTimeline: boolean;
  };
  createdAt: Date;
  updatedAt: Date;
}

export interface ExportPreset {
  id: string;
  name: string;
  width: number;
  height: number;
  fps: number;
  format: string;
  codec: string;
  bitrate: string;
  audioBitrate: string;
}

export interface RenderJob {
  id: string;
  projectId: string;
  preset: ExportPreset;
  status: 'pending' | 'processing' | 'completed' | 'failed';
  progress: number;
  downloadUrl?: string;
  error?: string;
  createdAt: Date;
  completedAt?: Date;
}

export interface TimelineState {
  currentTime: number;
  zoom: number;
  scrollX: number;
  selectedClips: string[];
  selectedTracks: string[];
  inPoint?: number;
  outPoint?: number;
  isPlaying: boolean;
  isLooping: boolean;
}

export interface ViewerState {
  safeMargins: boolean;
  rulers: boolean;
  guides: boolean;
  backgroundColor: string;
}

client/src/utils/timecode.ts.content:
export function formatTimecode(seconds: number, fps: number = 30): string {
  const totalFrames = Math.floor(seconds * fps);
  const hours = Math.floor(totalFrames / (fps * 3600));
  const minutes = Math.floor((totalFrames % (fps * 3600)) / (fps * 60));
  const secs = Math.floor((totalFrames % (fps * 60)) / fps);
  const frames = totalFrames % fps;

  return `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}:${frames.toString().padStart(2, '0')}`;
}

export function parseTimecode(timecode: string, fps: number = 30): number {
  const parts = timecode.split(':').map(Number);
  if (parts.length !== 4) return 0;

  const [hours, minutes, seconds, frames] = parts;
  return hours * 3600 + minutes * 60 + seconds + frames / fps;
}

export function formatTime(seconds: number): string {
  const hours = Math.floor(seconds / 3600);
  const minutes = Math.floor((seconds % 3600) / 60);
  const secs = Math.floor(seconds % 60);

  if (hours > 0) {
    return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  }
  return `${minutes}:${secs.toString().padStart(2, '0')}`;
}

export function secondsToFrames(seconds: number, fps: number): number {
  return Math.floor(seconds * fps);
}

export function framesToSeconds(frames: number, fps: number): number {
  return frames / fps;
}

client/src/components/auth/auth-forms.ts.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { loginSchema, signupSchema, type LoginData, type SignupData } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Loader2 } from "lucide-react";

export function AuthForms() {
  const { login, signup, isLoginLoading, isSignupLoading } = useAuth();
  const { toast } = useToast();

  const loginForm = useForm<LoginData>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      email: "",
      password: "",
    },
  });

  const signupForm = useForm<SignupData>({
    resolver: zodResolver(signupSchema),
    defaultValues: {
      email: "",
      password: "",
      username: "",
      firstName: "",
      lastName: "",
    },
  });

  const onLogin = async (data: LoginData) => {
    try {
      await login(data);
      toast({
        title: "Login berhasil",
        description: "Selamat datang kembali!",
      });
    } catch (error: any) {
      toast({
        variant: "destructive",
        title: "Login gagal",
        description: error.message || "Email atau password salah",
      });
    }
  };

  const onSignup = async (data: SignupData) => {
    try {
      await signup(data);
      toast({
        title: "Registrasi berhasil",
        description: "Akun Anda telah dibuat!",
      });
    } catch (error: any) {
      toast({
        variant: "destructive",
        title: "Registrasi gagal",
        description: error.message || "Terjadi kesalahan saat membuat akun",
      });
    }
  };

  return (
    <div className="w-full max-w-md mx-auto">
      <Tabs defaultValue="login" className="w-full">
        <TabsList className="grid w-full grid-cols-2" data-testid="auth-tabs">
          <TabsTrigger value="login" data-testid="tab-login">Login</TabsTrigger>
          <TabsTrigger value="signup" data-testid="tab-signup">Daftar</TabsTrigger>
        </TabsList>

        <TabsContent value="login">
          <Card>
            <CardHeader>
              <CardTitle>Login</CardTitle>
              <CardDescription>
                Masuk ke akun VideoAI Anda
              </CardDescription>
            </CardHeader>
            <form onSubmit={loginForm.handleSubmit(onLogin)}>
              <CardContent className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="login-email">Email</Label>
                  <Input
                    id="login-email"
                    type="email"
                    placeholder="nama@email.com"
                    {...loginForm.register("email")}
                    data-testid="input-login-email"
                  />
                  {loginForm.formState.errors.email && (
                    <p className="text-sm text-red-600" data-testid="error-login-email">
                      {loginForm.formState.errors.email.message}
                    </p>
                  )}
                </div>
                <div className="space-y-2">
                  <Label htmlFor="login-password">Password</Label>
                  <Input
                    id="login-password"
                    type="password"
                    placeholder="Password Anda"
                    {...loginForm.register("password")}
                    data-testid="input-login-password"
                  />
                  {loginForm.formState.errors.password && (
                    <p className="text-sm text-red-600" data-testid="error-login-password">
                      {loginForm.formState.errors.password.message}
                    </p>
                  )}
                </div>
              </CardContent>
              <CardFooter>
                <Button 
                  type="submit" 
                  className="w-full" 
                  disabled={isLoginLoading}
                  data-testid="button-login-submit"
                >
                  {isLoginLoading && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                  Masuk
                </Button>
              </CardFooter>
            </form>
          </Card>
        </TabsContent>

        <TabsContent value="signup">
          <Card>
            <CardHeader>
              <CardTitle>Daftar Akun</CardTitle>
              <CardDescription>
                Buat akun VideoAI baru
              </CardDescription>
            </CardHeader>
            <form onSubmit={signupForm.handleSubmit(onSignup)}>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="signup-firstname">Nama Depan</Label>
                    <Input
                      id="signup-firstname"
                      placeholder="Nama depan"
                      {...signupForm.register("firstName")}
                      data-testid="input-signup-firstname"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="signup-lastname">Nama Belakang</Label>
